	    function activateBanner(banner) {
	        for (var i = 0; i < 17; i++) {
	            document.getElementById("banner" + (i+1)).className = "banner";
	        }
			
	        document.getElementById(banner).className = "banner active";
		}

		function activateTab(e) {
