			document.getElementById("li_content2").className = "";
			document.getElementById("li_content3").className = "";
			document.getElementById("li_content4").className = "";
			document.getElementById("li_content5").className = "";
			document.getElementById(e.getAttribute("data-tab")).className = "active";
		}

		function activateImage(e) {
			mainImage.src = e.src.swap("/45/", "/300/");
			mainImageLink.href = e.src.swap("/45/", "/0/");
		}

		var month = [
            "January",
            "February",
            "March",
            "April",
            "May",
            "June",
            "July",
            "August",
            "September",
            "October",
            "November",
            "December"
        ];

        var day = [
            "Sunday",
            "Monday",
            "Tuesday",
            "Wednesday",
            "Thursday",
            "Friday",
            "Saturday"
        ];

        var nonWorkingDays = [
            // 2012
            new Date(2012, 3, 6), //Good Friday
            new Date(2012, 3, 9), //Easter Monday
            new Date(2012, 4, 7), //Early May Bank Holiday
            new Date(2012, 5, 4), //Spring Bank Holiday
            new Date(2012, 5, 5), //Queen''s Diamond Jubilee
            new Date(2012, 7, 27), //Summer Bank Holiday
            new Date(2012, 11, 25), //Christmas Day
            new Date(2012, 11, 26), //Boxing Day
			// 2013
			new Date(2013, 0, 1),	//New Year’s Day
			new Date(2013, 0, 2),	//New Year’s Day
			new Date(2013, 2, 29), //Good Friday
			new Date(2013, 3, 1), //Easter Monday
			new Date(2013, 4, 6), //Early May bank holiday
			new Date(2013, 4, 27), //Spring bank holiday
			new Date(2013, 7, 26), //Summer bank holiday
            new Date(2013, 11, 23), //Christmas Eve Eve
            new Date(2013, 11, 24), //Christmas Eve
			new Date(2013, 11, 25), //Christmas Day
			//new Date(2013, 11, 26), //Boxing Day
            new Date(2013, 11, 31), //New Year''s Eve
			// 2014
			new Date(2014, 0, 1), //New Year’s Day
			new Date(2014, 3, 18), //Good Friday
			new Date(2014, 3, 21), //Easter Monday
			new Date(2014, 4, 5), //Early May bank holiday
			new Date(2014, 4, 26), //Spring bank holiday
			new Date(2014, 7, 25), //Summer bank holiday
			new Date(2014, 11, 25), //Christmas Day
			new Date(2014, 11, 26), //Boxing Day
			// 2015
			new Date(2015, 0, 1), //New Year’s Day
			new Date(2015, 3, 3), //Good Friday
			new Date(2015, 3, 6), //Easter Monday
			new Date(2015, 4, 4), //Early May bank holiday
			new Date(2015, 4, 25), //Spring bank holiday
			new Date(2015, 7, 31), //Summer bank holiday
			new Date(2015, 11, 25), //Christmas Day
			new Date(2015, 11, 28) //Boxing Day Substitute day
        ];
        
        var dispatchDate = new Date();
        var deliveryDate = new Date();
		var currentDate = new Date();

		window.onload = function () {
			dispatchDate.setHours(13);
            dispatchDate.setMinutes(00);

			if(currentDate < dispatchDate){
				currentDate.setDate(currentDate.getDate() - 1);
			}
			
            dispatchDate = GetNextWorkingDay(currentDate);
			deliveryDate = GetNextWorkingDay(dispatchDate);
            SetTime();
		    
            document.getElementById(''DispatchDay'').innerHTML = day[deliveryDate.getDay()] + '' '' + deliveryDate.getDate() + '' '' + month[deliveryDate.getMonth()] + '' '' + deliveryDate.getFullYear() + '' *'';
            
			setInterval(nextBanner, 3000);

			document.getElementById("copyrightYear").innerHTML = new Date().getFullYear();
		};
		
        Array.prototype.Contains = function(obj) {
            var i = this.length;
            while (i--) {
                if (this[i].toUTCString() == obj.toUTCString()) {
                    return true;
                }
            }
            return false;
        };

		function IsWorkingDay(date){
			return !(date.getDay() == 6 || date.getDay() == 0 || nonWorkingDays.Contains(new Date(date.getFullYear(), date.getMonth(), date.getDate())));
		}

		function GetNextWorkingDay(date){
			var newDate = new Date(date.getTime());
			newDate.setDate(newDate.getDate() + 1);
			while(!IsWorkingDay(newDate)){
				newDate.setDate(newDate.getDate() + 1);
			}
			return newDate;
		}

        function SetTime() {
			dispatchDate.setHours(13);
            dispatchDate.setMinutes(00);
            var amount = dispatchDate.getTime() - new Date().getTime();
            var hours = 0, mins = 0, secs = 0;
            amount = Math.floor(amount / 1000); //secs
            hours = Math.floor(amount / 3600); //hours
            amount = amount % 3600;
            mins = Math.floor(amount / 60); //minutes
            amount = amount % 60;
            secs = Math.floor(amount % 60) //seconds
            document.getElementById(''within_hours'').innerHTML = hours;
			document.getElementById(''within_mins'').innerHTML = mins;
			document.getElementById(''within_seconds'').innerHTML = secs;

            setTimeout(function () { SetTime(); }, 1000);
        }

		var currentBanner = 1;
		function nextBanner(){
			activateBanner("banner" + ++currentBanner);
			if(currentBanner > 16) currentBanner = 0;
		}

		String.prototype.swap = function (targetString, newString) {
			var i = this.indexOf(targetString);
			return this.substr(0, i) + newString + this.substr(i + targetString.length);
		}

	</script>

