INSERT Items(ID,Titel,Categorie,Postcode,Locatie,Land,Verkoper,Prijs,Valuta,Conditie,Thumbnail,Beschrijving) VALUES (311178201433,'ALS NIEUW! Boek 365 SPOKEN ´Elke dag een verhaaltje´ REBO Christl Vogl',17157,'9190','België','BE','2013pinkfriday','1.99','EUR','Vrijwel nieuw','img311178201433.jpg','<P align=center><FONT face="Comic Sans MS">In nieuwstaat! Zeer mooi en stevig, groot boek.</FONT></P>
