INSERT Items(ID,Titel,Categorie,Postcode,Locatie,Land,Verkoper,Prijs,Valuta,Conditie,Thumbnail,Beschrijving) VALUES (271668148018,'Unique army piece of the second world war',87604,'5611PR','Nederland','NL','vincer2005','3499.0','EUR','Vrijwel nieuw','img271668148018.jpg','<span style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 13px; background-color: rgb(241, 244, 248);">Geachte heer of dame,</span><br style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 13px; background-color: rgb(241, 244, 248);"><br style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 13px; background-color: rgb(241, 244, 248);"><span style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 13px; background-color: rgb(241, 244, 248);">Hierbij wil ik met pijn in mijn hard, een erfstuk aanbieden dat ik ontvangen heb na de dood van mijn dierbare oma. Het is mij verteld dat ik dit in principe ook niet mocht verkopen, echter door het gebrek aan geld, heb ik toch besloten dit te doen.&nbsp;</span><br style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 13px; background-color: rgb(241, 244, 248);"><br style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 13px; background-color: rgb(241, 244, 248);"><span style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 13px; background-color: rgb(241, 244, 248);">Het betreft hier een landkaart van de duitse stad "Wesel" nabij Essen en Duisburg. Dit betreft een tactiek landkaart van het leger. Hierop staan onder andere de soorten grond, waar luchtbewaking plaatsvindt etc. Zie voor meer informatie de foto''s.</span><br style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 13px; background-color: rgb(241, 244, 248);"><br style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 13px; background-color: rgb(241, 244, 248);"><span style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 13px; background-color: rgb(241, 244, 248);">De kaart is alleen ter verzending aangeboden, dit heb ik zo gekozen om anoniem te blijven, ik wil namelijk niet dat er mensen zijn die weten waar de kaart ofwel vandaan komt, ofwel dat mijn familie weet dat ik dit erfstuk verkoop.</span><br style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 13px; background-color: rgb(241, 244, 248);"><br style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 13px; background-color: rgb(241, 244, 248);"><span style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 13px; background-color: rgb(241, 244, 248);">Indien interesse ontvang ik graag een reactie.</span><br style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 13px; background-color: rgb(241, 244, 248);"><br style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 13px; background-color: rgb(241, 244, 248);"><span style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 13px; background-color: rgb(241, 244, 248);">Met vriendelijke groet,</span><br style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 13px; background-color: rgb(241, 244, 248);"><br style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 13px; background-color: rgb(241, 244, 248);"><span style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 13px; background-color: rgb(241, 244, 248);">Vince&nbsp;</span>')
